import { render, Portal } from "solid-js/web";
import "./styles.css";

function App() {
  return (
    <div class="app-container">
      <p>Just some text inside a div that has a restricted size.</p>
      <div class="popup">
        <h1>Popup</h1>
        <p>Some text you might need for something or other.</p>
      </div>
    </div>
  );
}

render(() => <App />, document.getElementById("app"));

.app-container {
  width: 200px;
  height: 100px;
  overflow: hidden;
}

.popup {
  position: relative;
  z-index: 2;
  background: #ddd;
  padding: 1rem;
  min-height: 200px;
  min-width: 200px;
}

.popup::after {
  content: " ";
  position: absolute;
  bottom: 100%;
  left: 50%;
  margin-left: -5px;
  border-width: 5px;
  border-style: solid;
  border-color: transparent transparent #ddd transparent;
}
